/* View: VISATENDIMENTOS */
CREATE VIEW VISATENDIMENTOS(
    CODIGOATENDIMENTO,
    CODIGOSOLICITANTE,
    NOMESOLICITANTE,
    CODIGOPROPRIEDADE,
    NOMEPROPRIEDADE,
    NOMEPONTOATENDIMENTO,
    NOMEPESSOABENEFICIADA,
    LOCALIDADEBENEFICIADA,
    DATACADASTRO,
    STATUSATENDIMENTO,
    OBSERVACOES)
AS
SELECT TABATENDIMENTOS.CODIGOATENDIMENTO,
       TABATENDIMENTOS.CODIGOSOLICITANTE,
       t1.NOMEPESSOA,
       TABPROPRIEDADES.CODIGOPROPRIEDADE,
       TABPROPRIEDADES.NOMEPROPRIEDADE,
       tabpontosatendimentos.nome,
       t2.nomepessoa,
       tablocalidades.nome,
       TABATENDIMENTOS.DATACADASTRO,
       CASE (select count(STATUSSERVICO) FROM TABSERVICOSATENDIMENTOS
            WHERE (TABATENDIMENTOS.CODIGOATENDIMENTO = TABSERVICOSATENDIMENTOS.CODIGOATENDIMENTO)
                  AND (TABSERVICOSATENDIMENTOS.STATUSSERVICO IN ('1', '2', '4')))

            WHEN 0 THEN TRIM('CONCLUDO')
            ELSE TRIM('PENDENTE')
       END,
       TABATENDIMENTOS.OBSERVACOES
FROM   TABATENDIMENTOS
JOIN   tabpessoas as t1 ON t1.CODIGOPESSOA = TABATENDIMENTOS.CODIGOSOLICITANTE
LEFT   JOIN TABPROPRIEDADES ON TABPROPRIEDADES.CODIGOPROPRIEDADE = TABATENDIMENTOS.CODIGOPROPRIEDADE
LEFT   JOIN tabpontosatendimentos ON tabpontosatendimentos.codigo = tabatendimentos.codigopontoatendimento
left   join tabpessoas as t2 on t2.codigopessoa = tabatendimentos.codigopessoabeneficiada
left   join tablocalidades on tablocalidades.codigolocalidade = tabatendimentos.codigolocalidade
;



/* View: VISATENDIMENTOSSERVICOS */
CREATE VIEW VISATENDIMENTOSSERVICOS(
    CODIGOATENDIMENTO,
    NOMESOLICITANTE,
    NOMEPROPRIEDADE,
    RESPONSAVELPROPRIEDADE,
    NOMEPONTOATENDIMENTO,
    NOMEPESSOABENEFICIADA,
    LOCALIDADEBENEFICIADA,
    LOCALIDADEDOBENEFICIADO,
    DISTRITODOBENEFICIADO,
    SERVICO,
    GRUPO,
    SUBGRUPO,
    TECNICORESPONSAVEL,
    RESPONSAVELATENDIMENTO,
    OBJETIVOATENDIMENTO,
    QUANTIDADESOLICITADA,
    QUANTIDADEATENDIDA,
    UNIDADEMEDIDA,
    VALORSERVICO,
    DATAATENDIMENTO,
    DATAPREVISTA,
    DATAEXECUCAO,
    DESCRICAO,
    STATUSSERVICO,
    UTM)
AS
SELECT TABATENDIMENTOS.CODIGOATENDIMENTO,
       SOLICITANTES.NOMEPESSOA,
       TABPROPRIEDADES.NOMEPROPRIEDADE,
       RESPONSAVEIS.NOMEPESSOA,
       tabpontosatendimentos.nome,
       PESSOABENEFICIADA.nomepessoa,
       LOCALIDADEBENEFICIADA.NOME,
       case TABATENDIMENTOS.TIPOBENEFICIADO
         when 'PROPRIEDADE' then LOCALIDADEPROPRIEDADE.NOME
         when 'PONTO DE ATENDIMENTO' then LOCALIDADEPONTO.NOME
         when 'PESSOA BENEFICIADA' then LOCALIDADEPESSOA.NOME
         when 'LOCALIDADE BENEFICIADA' then LOCALIDADEBENEFICIADA.NOME
       end,
       case TABATENDIMENTOS.TIPOBENEFICIADO
         when 'PROPRIEDADE' then LOCALIDADEPROPRIEDADE.DISTRITO
         when 'PONTO DE ATENDIMENTO' then LOCALIDADEPONTO.DISTRITO
         when 'PESSOA BENEFICIADA' then LOCALIDADEPESSOA.DISTRITO
         when 'LOCALIDADE BENEFICIADA' then LOCALIDADEBENEFICIADA.DISTRITO
       end,
       TABSERVICOS.DESCRICAO,
       TABSERVICOS.GRUPO,
       TABSERVICOS.SUBGRUPO,
       TABSERVICOSATENDIMENTOS.TECNICORESPONSAVEL,
       TABSERVICOSATENDIMENTOS.RESPONSAVELATENDIMENTO,
       TABSERVICOSATENDIMENTOS.OBJETIVOATENDIMENTO,
       TABSERVICOSATENDIMENTOS.QUANTIDADESOLICITADA,
       TABSERVICOSATENDIMENTOS.QUANTIDADEATENDIDA,
       TABSERVICOS.UNIDADEMEDIDA,
       TABSERVICOSATENDIMENTOS.VALORSERVICO,
       TABATENDIMENTOS.DATACADASTRO,
       TABSERVICOSATENDIMENTOS.DATAPREVISTA,
       TABSERVICOSATENDIMENTOS.DATAEXECUCAO,
       TABSERVICOSATENDIMENTOS.DESCRICAO,
       CASE TABSERVICOSATENDIMENTOS.STATUSSERVICO
            WHEN '1' THEN TRIM('SOLICITADO')
            WHEN '2' THEN TRIM('APROVADO')
            WHEN '3' THEN TRIM('REJEITADO')
            WHEN '4' THEN TRIM('EM ANDAMENTO')
            WHEN '5' THEN TRIM('FINALIZADO')
       END,
       (SELECT OUTUTM
        FROM PROCFORMATAUTM(TABSERVICOSATENDIMENTOS.UTMZONA,
                            TABSERVICOSATENDIMENTOS.UTMDESIGNADORZONA,
                            TABSERVICOSATENDIMENTOS.UTMLESTE,
                            TABSERVICOSATENDIMENTOS.UTMNORTE))
FROM TABATENDIMENTOS
JOIN TABSERVICOSATENDIMENTOS ON TABSERVICOSATENDIMENTOS.CODIGOATENDIMENTO = TABATENDIMENTOS.CODIGOATENDIMENTO
JOIN TABSERVICOS ON TABSERVICOS.CODIGOSERVICO = TABSERVICOSATENDIMENTOS.CODIGOSERVICO
JOIN TABPESSOAS SOLICITANTES ON SOLICITANTES.CODIGOPESSOA = TABATENDIMENTOS.CODIGOSOLICITANTE
LEFT JOIN TABPROPRIEDADES ON TABPROPRIEDADES.CODIGOPROPRIEDADE = TABATENDIMENTOS.CODIGOPROPRIEDADE
LEFT JOIN TABPESSOASPROPRIEDADES ON TABPESSOASPROPRIEDADES.CODIGOPROPRIEDADE = TABPROPRIEDADES.CODIGOPROPRIEDADE
          AND TABPESSOASPROPRIEDADES.RESPONSAVELPROPRIEDADE = 'S'
LEFT JOIN TABPESSOAS RESPONSAVEIS ON RESPONSAVEIS.CODIGOPESSOA = TABPESSOASPROPRIEDADES.CODIGOPESSOA
left join tabpontosatendimentos on tabpontosatendimentos.codigo = tabatendimentos.codigopontoatendimento
left join tabenderecos on tabenderecos.codigo = tabpontosatendimentos.codigoendereco
left join tabpessoas as PESSOABENEFICIADA on PESSOABENEFICIADA.codigopessoa = tabatendimentos.codigopessoabeneficiada
left join tablocalidades as LOCALIDADEBENEFICIADA on LOCALIDADEBENEFICIADA.codigolocalidade = tabatendimentos.codigolocalidade
left join TABLOCALIDADES on TABPROPRIEDADES.CODIGOLOCALIDADE = TABLOCALIDADES.CODIGOLOCALIDADE

left join TABLOCALIDADES as LOCALIDADEPROPRIEDADE on LOCALIDADEPROPRIEDADE.CODIGOLOCALIDADE = TABPROPRIEDADES.CODIGOLOCALIDADE
left join TABLOCALIDADES as LOCALIDADEPONTO on LOCALIDADEPONTO.CODIGOLOCALIDADE = TABENDERECOS.CODIGOLOCALIDADE
left join TABLOCALIDADES as LOCALIDADEPESSOA on LOCALIDADEPESSOA.CODIGOLOCALIDADE = PESSOABENEFICIADA.CODIGOLOCALIDADE
;



