/* View: VISLOCALIDADESATENDIMENTOS */
CREATE VIEW VISLOCALIDADESATENDIMENTOS(
    CODIGOLOCALIDADE,
    NOME,
    DISTRITO,
    SETOR,
    MUNICIPIO,
    ESTADO,
    QTDATENDIMENTOS)
AS
select tablocalidades.codigolocalidade,
       tablocalidades.nome,
       tablocalidades.distrito,
       tablocalidades.setor,
       tablocalidades.municipio,
       tablocalidades.estado,
       COALESCE(QTDEATENDIMENTOSPROPRIEDADE, 0) + COALESCE(QTDEATENDIMENTOSPONTO, 0) +
       coalesce(QTDEATENDIMENTOSPESSOABENEFICIADA, 0) + coalesce(QTDEATENDIMENTOSLOCALIDADEBENEFICIADA, 0)
from tablocalidades
LEFT JOIN (
            SELECT TABPROPRIEDADES.CODIGOLOCALIDADE, COUNT(TABATENDIMENTOS.CODIGOATENDIMENTO) AS QTDEATENDIMENTOSPROPRIEDADE
            FROM TABATENDIMENTOS, TABLOCALIDADES
            JOIN TABPROPRIEDADES ON TABATENDIMENTOS.CODIGOPROPRIEDADE = TABPROPRIEDADES.CODIGOPROPRIEDADE
            WHERE TABLOCALIDADES.CODIGOLOCALIDADE = TABPROPRIEDADES.CODIGOLOCALIDADE
            GROUP BY 1
            )AS PROPRIEDADES ON PROPRIEDADES.CODIGOLOCALIDADE = TABLOCALIDADES.CODIGOLOCALIDADE
LEFT JOIN (
            SELECT  TABLOCALIDADES.CODIGOLOCALIDADE, COUNT(TABATENDIMENTOS.CODIGOATENDIMENTO) AS QTDEATENDIMENTOSPONTO
            FROM    TABATENDIMENTOS
            join    tabpontosatendimentos on tabpontosatendimentos.codigo = tabatendimentos.codigopontoatendimento
            join    tabenderecos on tabenderecos.codigo = tabpontosatendimentos.codigoendereco
            JOIN    TABLOCALIDADES ON tabenderecos.codigolocalidade = TABLOCALIDADES.codigolocalidade
            GROUP BY 1
            ) AS PONTOS ON PONTOS.CODIGOLOCALIDADE = TABLOCALIDADES.CODIGOLOCALIDADE
LEFT JOIN (
            SELECT  TABLOCALIDADES.CODIGOLOCALIDADE, COUNT(TABATENDIMENTOS.CODIGOATENDIMENTO) AS QTDEATENDIMENTOSPESSOABENEFICIADA
            FROM    TABATENDIMENTOS
            join    tabpessoas on tabpessoas.codigopessoa = tabatendimentos.codigopessoabeneficiada
            JOIN    TABLOCALIDADES ON tabpessoas.codigolocalidade = TABLOCALIDADES.codigolocalidade
            GROUP BY 1
            ) AS PESSOAS ON PESSOAS.CODIGOLOCALIDADE = TABLOCALIDADES.CODIGOLOCALIDADE
LEFT JOIN (
            SELECT  TABLOCALIDADES.CODIGOLOCALIDADE, COUNT(TABATENDIMENTOS.CODIGOATENDIMENTO) AS QTDEATENDIMENTOSLOCALIDADEBENEFICIADA
            FROM    TABATENDIMENTOS
            JOIN    TABLOCALIDADES ON tabatendimentos.codigolocalidade = TABLOCALIDADES.codigolocalidade
            GROUP BY 1
            ) AS LOCALIDADESBENEFICIADAS ON LOCALIDADESBENEFICIADAS.CODIGOLOCALIDADE = TABLOCALIDADES.CODIGOLOCALIDADE
;



