/* View: VISPRODUTORES */
CREATE VIEW VISPRODUTORES(
    CODIGO,
    NOME,
    TIPOPESSOA,
    INSCRICAOESTADUAL,
    CPFCNPJ,
    RG,
    SITUACAO,
    VINCULO,
    DATAFIMCONTRATO,
    DATACADASTRO,
    DATAULTIMANOTA,
    CNPJQUILOMBO,
    LOGRADOURO,
    BAIRRO,
    CEP,
    NOMELOCALIDADE,
    DISTRITO,
    TELEFONE,
    OBSERVACAO)
AS
SELECT TABPESSOAS.CODIGOPESSOA,
       TABPESSOAS.NOMEPESSOA,
       TABPESSOAS.TIPOPESSOA,
       TABPESSOASPROPRIEDADES.INSCRICAOESTADUAL,
       TABPESSOAS.CPFCNPJ,
       TABPESSOAS.RG,
       CASE TABPESSOASPROPRIEDADES.SITUACAO
         WHEN '0' THEN TRIM('INATIVO')
         WHEN '1' THEN TRIM('ATIVO')
       END,
       CASE TABPESSOASPROPRIEDADES.TIPOVINCULO
           WHEN '1'  THEN TRIM('PROPRIETRIO')
           WHEN '2'  THEN TRIM('CONDMINO')
           WHEN '3'  THEN TRIM('ARRENDATRIO')
           WHEN '4'  THEN TRIM('USUFRUTRIO')
           WHEN '5'  THEN TRIM('PARCEIRO')
           WHEN '6'  THEN TRIM('COMODATRIO')
           WHEN '7'  THEN TRIM('PESCADOR')
           WHEN '8'  THEN TRIM('POSSEIRO')
           WHEN '9'  THEN TRIM('NU PROPRIETRIO')
           WHEN '10'  THEN TRIM('MUTURIO')
           WHEN '11'  THEN TRIM('QUILOMBOLA')
           WHEN '12'  THEN TRIM('CO-PROPRIETRIO')
           WHEN '13'  THEN TRIM('ASSENTADO')
           WHEN '14'  THEN TRIM('ASSALARIADO')
        end,
       TABPESSOASPROPRIEDADES.DATAFIMCONTRATO,
       tabpessoas.datacadastro,
       TABPESSOASPROPRIEDADES.DATAULTIMANOTA,
       tabpessoaspropriedades.cnpjquilombo,
       TABPESSOAS.TIPOLOGRADOURO || ' ' || TABPESSOAS.LOGRADOURO || ', ' || TABPESSOAS.NUMEROIMOVEL,
       TABPESSOAS.BAIRRO,
       TABPESSOAS.CEP,
       TABLOCALIDADES.NOME,
       TABLOCALIDADES.DISTRITO,
       TABPESSOAS.TELEFONE,
       TABPESSOAS.OBSERVACAO
FROM   TABPESSOAS
JOIN   TABPESSOASPROPRIEDADES ON TABPESSOAS.CODIGOPESSOA = TABPESSOASPROPRIEDADES.CODIGOPESSOA and
                                 TABPESSOASPROPRIEDADES.CADASTROATUALIZADO = 'S'
JOIN   TABLOCALIDADES ON TABLOCALIDADES.CODIGOLOCALIDADE = TABPESSOAS.CODIGOLOCALIDADE
WHERE  TABPESSOASPROPRIEDADES.INSCRICAOESTADUAL IS NOT NULL
;



