CREATE VIEW VISPESSOASSEMINSCRICAO(
    CODIGO,
    NOME,
    TIPOPESSOA,
    CPFCNPJ,
    RG,
    DATANASCIMENTO,
    DATACADASTRO,
    SITUACAO,
    OBSERVACAO)
AS
SELECT tabpessoas.codigopessoa,
       NOMEPESSOA,
       TIPOPESSOA,
       CPFCNPJ,
       RG, 
       DATANASCIMENTO, 
       DATACADASTRO, 
       TRIM(
         CASE SITUACAO
            WHEN '1' THEN 'ATIVO'
            WHEN '0' THEN 'INATIVO'
         END) AS SITUACAO,
       OBSERVACAO
