            where (tabpessoaspropriedades.codigopessoa = tabpessoas.codigopessoa)
            and (tabpessoaspropriedades.inscricaoestadual is null)) <> 0)
and
not exists (select TABSOCIOS.CODIGOSOCIO
               from   TABSOCIOS
               where  TABSOCIOS.CODIGOSOCIO = TABPESSOAS.CODIGOPESSOA)
;



