        CASE TABPESSOASPROPRIEDADES.SITUACAO
            WHEN '0' THEN TRIM('INATIVO')
            WHEN '1' THEN TRIM('ATIVO')
        END,
        TABPESSOAS.DATACADASTRO,
        TABPESSOASPROPRIEDADES.DATAULTIMANOTA,
        TABPROPRIEDADES.NOMEPROPRIEDADE,
        CASE
            WHEN TABPROPRIEDADES.TIPODOCUMENTO = 'INCRA' THEN TABPROPRIEDADES.NUMERODOCUMENTO
            WHEN TABPROPRIEDADES.TIPODOCUMENTOALTERNATIVO = 'INCRA' THEN TABPROPRIEDADES.NUMERODOCUMENTOALTERNATIVO
        END AS NUMEROINCRA,
        CASE
            WHEN TABPROPRIEDADES.TIPODOCUMENTO <> 'INCRA' THEN TABPROPRIEDADES.TIPODOCUMENTO
            WHEN TABPROPRIEDADES.TIPODOCUMENTOALTERNATIVO <> 'INCRA' THEN TABPROPRIEDADES.TIPODOCUMENTOALTERNATIVO
        END AS TIPODOCUMENTOALTERNATIVO,
        CASE
            WHEN TABPROPRIEDADES.TIPODOCUMENTO <> 'INCRA' THEN TABPROPRIEDADES.NUMERODOCUMENTO
            WHEN TABPROPRIEDADES.TIPODOCUMENTOALTERNATIVO <> 'INCRA' THEN TABPROPRIEDADES.NUMERODOCUMENTOALTERNATIVO
        END AS NUMERODOCUMENTOALTERNATIVO,
        tablocalidades.nome,
        tablocalidades.distrito,
        tablocalidades.setor

FROM    TABPESSOAS
JOIN    TABPESSOASPROPRIEDADES ON TABPESSOASPROPRIEDADES.CODIGOPESSOA = TABPESSOAS.CODIGOPESSOA and
                                  TABPESSOASPROPRIEDADES.CADASTROATUALIZADO = 'S'
JOIN    TABPROPRIEDADES ON TABPROPRIEDADES.CODIGOPROPRIEDADE = TABPESSOASPROPRIEDADES.CODIGOPROPRIEDADE
join    tablocalidades on tablocalidades.codigolocalidade = tabpropriedades.codigolocalidade
WHERE   TABPESSOASPROPRIEDADES.INSCRICAOESTADUAL IS NOT NULL
;



