/* View: VISPRODUTOSNOTASFISCAIS */
CREATE VIEW VISPRODUTOSNOTASFISCAIS(
    CODIGOPRODUTO,
    CATEGORIA,
    CATEGORIASEFAZ,
    DESCRICAO,
    UNIDADEMEDIDA,
    QTDADEPRODUTO,
    QTDADENOTASFISCAIS,
    SOMANOTASFISCAIS)
AS
SELECT TABPRODUTOS.CODIGOPRODUTO, TABCATEGORIASPRODUTOS.DESCRICAO, TABCATEGORIASSEFAZ.DESCRICAO,
       TABPRODUTOS.DESCRICAO, TABUNIDADESMEDIDA.SIGLA, QUANTIDADEPRODUTOS, coalesce(QUANTIDADENOTAS, 0),
       VALORTOTAL
FROM TABPRODUTOS
LEFT OUTER JOIN (SELECT TABITENSNOTASFISCAIS.CODIGOPRODUTO AS CODIGO,
                        COUNT(TABNOTASFISCAIS.CODIGONOTAFISCAL) AS QUANTIDADENOTAS,
                        SUM((SELECT OUTQUANTIDADE
                         FROM PROCCONVERTEUNIDADE(
                                TABITENSNOTASFISCAIS.CODIGOUNIDADEMEDIDA,
                                TABITENSNOTASFISCAIS.QUANTIDADE,
                                TABITENSNOTASFISCAIS.CODIGOPRODUTO))) AS QUANTIDADEPRODUTOS,
                        SUM(TABITENSNOTASFISCAIS.VALOR) AS VALORTOTAL
                 FROM TABITENSNOTASFISCAIS, TABNOTASFISCAIS
                 WHERE TABNOTASFISCAIS.CODIGONOTAFISCAL = TABITENSNOTASFISCAIS.CODIGONOTAFISCAL
                 GROUP BY CODIGOPRODUTO) ON CODIGOPRODUTO = CODIGO
LEFT OUTER JOIN TABUNIDADESMEDIDA ON (TABPRODUTOS.CODIGOPRODUTO = TABUNIDADESMEDIDA.CODIGOPRODUTO AND
                                      TABUNIDADESMEDIDA.TIPO = 'PRINCIPAL')
JOIN TABCATEGORIASPRODUTOS ON TABPRODUTOS.CODIGOCATEGORIA = TABCATEGORIASPRODUTOS.CODIGOCATEGORIA
JOIN TABCATEGORIASSEFAZ ON TABPRODUTOS.CODIGOCATEGORIASEFAZ = TABCATEGORIASSEFAZ.CODIGOCATEGORIA
;



