/* View: VISSELECAOPROPRIEDADES */
CREATE VIEW VISSELECAOPROPRIEDADES(
    CODIGOPROPRIEDADE,
    NOMEPROPRIEDADE,
    NOMEPESSOA,
    INSCRICAOESTADUAL,
    TIPODOCPRINCIPAL,
    NUMERODOCPRINCIPAL,
    TIPOOUTRODOC,
    NUMEROOUTRODOC,
    OUTROSTITULARES,
    CONDICAO,
    STATUSRESPONSAVEL,
    NOMELOCALIDADE,
    DISTRITO,
    SETOR,
    DATAVALIDADERGP,
    SITUACAOIMOVEL,
    ULTIMANOTAIE,
    DATAATUALIZACAO,
    DATATERMINOCONTRATO,
    UTM,
    OBSERVACAO)
AS
SELECT TABPROPRIEDADES.CODIGOPROPRIEDADE,
       TABPROPRIEDADES.NOMEPROPRIEDADE,
       TABPESSOAS.NOMEPESSOA,
       TABPESSOASPROPRIEDADES.INSCRICAOESTADUAL,
       TABPROPRIEDADES.TIPODOCUMENTO,
       TABPROPRIEDADES.NUMERODOCUMENTO,
       TABPROPRIEDADES.TIPODOCUMENTOALTERNATIVO,
       TABPROPRIEDADES.NUMERODOCUMENTOALTERNATIVO,
       CASE TABPESSOASPROPRIEDADES.OUTROSTITULARESPRODUTOR
         WHEN 'S' THEN 'SIM'
         WHEN 'N' THEN 'NO'
       END,
       (select trim(OUTCONDICAO) from PROCFORMATARCONDICAOPRODUTOR(TABPESSOASPROPRIEDADES.TIPOVINCULO)),
       CASE TABPESSOASPROPRIEDADES.RESPONSAVELPROPRIEDADE
         WHEN 'S' THEN 'SIM'
         WHEN 'N' THEN 'NO'
       END,
       TABLOCALIDADES.NOME AS NOMELOCALIDADE,
       TABLOCALIDADES.DISTRITO,
       TABLOCALIDADES.SETOR,
       TABPROPRIEDADES.DATAVALIDADERGP,
       TABPROPRIEDADES.SITUACAOIMOVEL,
       tabpessoaspropriedades.DATAULTIMANOTA,
       TABPROPRIEDADES.DATAATUALIZACAO,
       tabpessoaspropriedades.datafimcontrato,
       (SELECT OUTUTM
        FROM PROCFORMATAUTM(TABPROPRIEDADES.UTMZONA,
                            TABPROPRIEDADES.UTMDESIGNADORZONA,
                            TABPROPRIEDADES.UTMLESTE,
                            TABPROPRIEDADES.UTMNORTE)),
       TABPROPRIEDADES.OBSERVACAO
FROM   TABPROPRIEDADES
join TABPESSOASPROPRIEDADES on TABPESSOASPROPRIEDADES.CODIGOPROPRIEDADE = TABPROPRIEDADES.CODIGOPROPRIEDADE and
                               TABPESSOASPROPRIEDADES.CADASTROATUALIZADO = 'S'
join TABPESSOAS on TABPESSOAS.CODIGOPESSOA = TABPESSOASPROPRIEDADES.CODIGOPESSOA
JOIN TABLOCALIDADES ON TABLOCALIDADES.CODIGOLOCALIDADE = tabpropriedades.codigolocalidade
;



