/* Trigger: TABNOTASFISCAIS_AIUD0 */
CREATE TRIGGER TABNOTASFISCAIS_AIUD0 FOR TABNOTASFISCAIS
ACTIVE AFTER INSERT OR UPDATE OR DELETE POSITION 0
AS
begin
   update TABLOTESNF
   set
      NUMNOTASSUCESSO = (select coalesce(count(TABNOTASFISCAIS.CODIGONOTAFISCAL), 0)
                         from   TABNOTASFISCAIS
                         where  TABNOTASFISCAIS.CODIGOLOTE = TABLOTESNF.CODIGOLOTE and
                                TABNOTASFISCAIS.STATUSENVIO in ('TRANSMITIDA', 'VALIDADA', 'INVALIDADA')),
      NUMNOTASFALHA = (select coalesce(count(TABNOTASFISCAIS.CODIGONOTAFISCAL), 0)
                       from   TABNOTASFISCAIS
                       where  TABNOTASFISCAIS.CODIGOLOTE = TABLOTESNF.CODIGOLOTE and
                              (TABNOTASFISCAIS.MOTIVORETORNO is not null and
                               TABNOTASFISCAIS.MOTIVORETORNO <> 'NFP PROCESSADA COM SUCESSO'));
end;

