/* Trigger: TGRTABAIDFS_AIUD1 */
CREATE TRIGGER TGRTABAIDFS_AIUD1 FOR TABAIDFS
ACTIVE BEFORE INSERT OR UPDATE OR DELETE POSITION 1
as
begin
   if (inserting or updating) then begin
      update TABPROCESSOS
      set    COMAIDF = 'S'
      where  CODIGOPROCESSO = new.CODIGOPROCESSO;
   end
   
   if (deleting) then begin
      update TABPROCESSOS
      set    COMAIDF = 'N'
      where  TABPROCESSOS.CODIGOPROCESSO = old.CODIGOPROCESSO and
             not exists (
               select TABAIDFS.CODIGOPROCESSO
               from   TABAIDFS
               where  TABAIDFS.CODIGOPROCESSO = old.CODIGOPROCESSO and
                      TABAIDFS.CODIGOAIDF <> old.CODIGOAIDF
            );
   end
end;

