/* Trigger: TGRULTIMANOTAPRODUTOR_BI0 */
CREATE TRIGGER TGRULTIMANOTAPRODUTOR_BI0 FOR TABNOTASFISCAIS
ACTIVE AFTER INSERT OR UPDATE OR DELETE POSITION 0
AS
  DECLARE VARIABLE DATA DATE;
  DECLARE VARIABLE DATAEX DATE;
BEGIN
  SELECT MAX(TABNOTASFISCAIS.DATAEMISSAO)
  FROM   TABNOTASFISCAIS
  WHERE  TABNOTASFISCAIS.INSCPRODUTOR = NEW.INSCPRODUTOR
  INTO   :DATA;

  SELECT MAX(TABNOTASFISCAIS.DATAEMISSAO)
  FROM   TABNOTASFISCAIS
  WHERE  TABNOTASFISCAIS.INSCPRODUTOR = OLD.INSCPRODUTOR
  INTO   :DATAEX;

  IF (INSERTING OR UPDATING) THEN
  BEGIN
     UPDATE TABPESSOASPROPRIEDADES
     SET    DATAULTIMANOTA = :DATA
     WHERE  INSCRICAOESTADUAL = NEW.INSCPRODUTOR;
  END

  IF (DELETING) THEN
  BEGIN
     UPDATE TABPESSOASPROPRIEDADES
     SET    DATAULTIMANOTA = :DATAEX
     WHERE  INSCRICAOESTADUAL = OLD.INSCPRODUTOR;
  END
end;

/* Trigger: TGRULTIMANOTAPROPRIEDADE_BI0 */
CREATE TRIGGER TGRULTIMANOTAPROPRIEDADE_BI0 FOR TABNOTASFISCAIS
ACTIVE AFTER INSERT OR UPDATE OR DELETE POSITION 0
AS
  DECLARE VARIABLE DATA DATE;
  DECLARE VARIABLE DATAEX DATE;
  DECLARE VARIABLE PROPRIEDADE INTEGER;
  DECLARE VARIABLE PROPRIEDADEEX INTEGER;
BEGIN
  SELECT MAX(TABNOTASFISCAIS.DATAEMISSAO)
  FROM   TABNOTASFISCAIS
  WHERE  TABNOTASFISCAIS.INSCPRODUTOR = NEW.INSCPRODUTOR
  INTO   :DATA;

  SELECT MAX(TABNOTASFISCAIS.DATAEMISSAO)
  FROM   TABNOTASFISCAIS
  WHERE  TABNOTASFISCAIS.INSCPRODUTOR = OLD.INSCPRODUTOR
  INTO   :DATAEX;

  SELECT CODIGOPROPRIEDADE
  FROM   TABPESSOASPROPRIEDADES
  WHERE  INSCRICAOESTADUAL = NEW.INSCPRODUTOR
  INTO   :PROPRIEDADE;

  SELECT CODIGOPROPRIEDADE
  FROM   TABPESSOASPROPRIEDADES
  WHERE  INSCRICAOESTADUAL = OLD.INSCPRODUTOR
  INTO   :PROPRIEDADEEX;

  IF (INSERTING OR UPDATING) THEN
  BEGIN
     UPDATE TABPROPRIEDADES
     SET    DATAULTIMANOTA = :DATA
     WHERE  CODIGOPROPRIEDADE = :PROPRIEDADE;
  END

  IF (DELETING) THEN
  BEGIN
     UPDATE TABPROPRIEDADES
     SET    DATAULTIMANOTA = :DATAEX
     WHERE  CODIGOPROPRIEDADE = :PROPRIEDADEEX;
  END
END;



