ALTER PROCEDURE PROCALTERARENDERECOPESSOA (
    INCODIGOPESSOA TYPE OF CODIGOS,
    INCODIGOPROPRIEDADE TYPE OF CODIGOS)
AS
declare variable TIPOLOGRADOURO type of LOGRADOUROS;
declare variable LOGRADOURO type of LOGRADOUROS;
declare variable NUMEROIMOVEL type of NUMEROSIMOVEL;
declare variable COMPLEMENTO type of LOGRADOUROS;
declare variable BAIRRO type of BAIRROS;
declare variable CEP type of CEPS;
declare variable REFERENCIA type of LOGRADOUROS;
declare variable CODIGOLOCALIDADE type of CODIGOS;
BEGIN
   SELECT CODIGOLOCALIDADE,
          TIPOLOGRADOURO,
          LOGRADOURO, 
          NUMEROIMOVEL, 
          COMPLEMENTO, 
          BAIRRO, 
          CEP, 
          REFERENCIA
   FROM TABPROPRIEDADES
   WHERE CODIGOPROPRIEDADE = :INCODIGOPROPRIEDADE
   INTO   :CODIGOLOCALIDADE,
          :TIPOLOGRADOURO,
          :LOGRADOURO, 
          :NUMEROIMOVEL, 
          :COMPLEMENTO, 
          :BAIRRO, 
          :CEP, 
          :REFERENCIA;

   IF (TIPOLOGRADOURO IS NULL) THEN BEGIN
      TIPOLOGRADOURO = '';
   END

   IF (LOGRADOURO IS NULL) THEN BEGIN
      LOGRADOURO = '';
   END

   UPDATE TABPESSOAS
   SET    CODIGOLOCALIDADE = :CODIGOLOCALIDADE,
          TIPOLOGRADOURO = :TIPOLOGRADOURO,
          LOGRADOURO = :LOGRADOURO,
          NUMEROIMOVEL = :NUMEROIMOVEL,
          COMPLEMENTO = :COMPLEMENTO,
          BAIRRO = :BAIRRO,
          CEP = :CEP,
          REFERENCIA = :REFERENCIA
   WHERE CODIGOPESSOA = :INCODIGOPESSOA;
END;


