RETURNS (
    OUTQUANTIDADE TYPE OF QUANTIDADES,
    OUTSIGLA TYPE OF UNIDADEMEDIDAITEM,
    OUTCODIGOPRODUTO TYPE OF CODIGOS)
AS
declare variable FATORCONVERSAO type of FATORESCONVERSAO;
declare variable ARREDONDAMENTO type of ARREDONDAMENTOS;
declare variable TIPO type of UNIDADESMEDIDA;
BEGIN
    SELECT CODIGOPRODUTO, SIGLA, TIPO, FATORCONVERSAO, ARREDONDAMENTO
    FROM TABUNIDADESMEDIDA
    WHERE CODIGOPRODUTO = :INCODIGOPRODUTO AND
              CODIGO = :INCODIGOUNIDADEMEDIDA
    INTO :OUTCODIGOPRODUTO, :OUTSIGLA, :TIPO, :FATORCONVERSAO, :ARREDONDAMENTO;

    IF (:ARREDONDAMENTO = 'ARREDONDAR') THEN BEGIN
          OUTQUANTIDADE = ROUND(INQUANTIDADE * FATORCONVERSAO, 2);
    END
    ELSE BEGIN
          OUTQUANTIDADE = TRUNC(INQUANTIDADE * FATORCONVERSAO);
    END

    IF (:TIPO = 'AUXILIAR') THEN BEGIN
          SELECT SIGLA
          FROM TABUNIDADESMEDIDA
          WHERE CODIGOPRODUTO = :INCODIGOPRODUTO AND
                            TIPO = 'PRINCIPAL'
          INTO :OUTSIGLA;
    END

    SUSPEND;
END;


ALTER PROCEDURE PROCEDUREACERTACADASTROPESSOA (
    INCODIGOPESSOAMASTER TYPE OF CODIGOS NOT NULL,
    INCODIGOPESSOA1 TYPE OF CODIGOS NOT NULL,
    INCODIGOPESSOA2 TYPE OF CODIGOS,
    INCODIGOPESSOA3 TYPE OF CODIGOS,
    INCODIGOPESSOA4 TYPE OF CODIGOS)
AS
BEGIN
  UPDATE TABPESSOASPROPRIEDADES
  SET    TABPESSOASPROPRIEDADES.CODIGOPESSOA = :INCODIGOPESSOAMASTER
  WHERE  CODIGOPESSOA = :INCODIGOPESSOA1;

  UPDATE TABSOCIOS
  SET    TABSOCIOS.codigosocio = :INCODIGOPESSOAMASTER
  WHERE  codigosocio = :INCODIGOPESSOA1;

  UPDATE TABTALOES
  SET    TABTALOES.CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOAMASTER
  WHERE  CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOA1;

  DELETE FROM TABPESSOAS
  WHERE  CODIGOPESSOA = :INCODIGOPESSOA1;

  IF (:INCODIGOPESSOA2 IS NOT NULL) THEN
  BEGIN
      UPDATE TABPESSOASPROPRIEDADES
      SET    TABPESSOASPROPRIEDADES.CODIGOPESSOA = :INCODIGOPESSOAMASTER
      WHERE  CODIGOPESSOA = :INCODIGOPESSOA2;

      UPDATE TABSOCIOS
      SET    TABSOCIOS.CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOAMASTER
      WHERE  CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOA2;

      UPDATE TABTALOES
      SET    TABTALOES.CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOAMASTER
      WHERE  CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOA2;

      DELETE FROM TABPESSOAS
      WHERE  CODIGOPESSOA = :INCODIGOPESSOA2;
  END

  IF (:INCODIGOPESSOA3 IS NOT NULL) THEN
  BEGIN
      UPDATE TABPESSOASPROPRIEDADES
      SET    TABPESSOASPROPRIEDADES.CODIGOPESSOA = :INCODIGOPESSOAMASTER
      WHERE  CODIGOPESSOA = :INCODIGOPESSOA3;

      UPDATE TABSOCIOS
      SET    TABSOCIOS.CODIGOSOCIO = :INCODIGOPESSOAMASTER
      WHERE  CODIGOSOCIO = :INCODIGOPESSOA3;

      UPDATE TABTALOES
      SET    TABTALOES.CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOAMASTER
      WHERE  CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOA3;

      DELETE FROM TABPESSOAS
      WHERE  CODIGOPESSOA = :INCODIGOPESSOA3;
  END

  IF (:INCODIGOPESSOA4 IS NOT NULL) THEN
  BEGIN
      UPDATE TABPESSOASPROPRIEDADES
      SET    TABPESSOASPROPRIEDADES.CODIGOPESSOA = :INCODIGOPESSOAMASTER
      WHERE  CODIGOPESSOA = :INCODIGOPESSOA4;

      UPDATE TABSOCIOS
      SET    TABSOCIOS.CODIGOSOCIO = :INCODIGOPESSOAMASTER
      WHERE  CODIGOSOCIO = :INCODIGOPESSOA4;

      UPDATE TABTALOES
      SET    TABTALOES.CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOAMASTER
      WHERE  CODIGOPESSOAPROPRIEDADE = :INCODIGOPESSOA4;

      DELETE FROM TABPESSOAS
      WHERE  CODIGOPESSOA = :INCODIGOPESSOA4;
  END
END;


