ALTER PROCEDURE PROCFORMATAUTM (
    INUTMZONA TYPE OF NUMEROSINTEIROS,
    INUTMDESIGNADORZONA TYPE OF ENUMERACOES,
    INUTMLESTE TYPE OF NUMEROSINTEIROS,
    INUTMNORTE TYPE OF NUMEROSINTEIROS)
RETURNS (
    OUTUTM VARCHAR(40))
AS
declare variable HEMISFERIO char(1);
begin
   if (upper(INUTMDESIGNADORZONA) < 'N') then
      HEMISFERIO = 'S';
   else
      HEMISFERIO = 'N';

   INUTMDESIGNADORZONA = upper(INUTMDESIGNADORZONA);
   OUTUTM = INUTMZONA || ' ' || INUTMDESIGNADORZONA || ' ' || INUTMLESTE || ' M E ' || INUTMNORTE || ' M ' || :HEMISFERIO;
   suspend;
end;


