ALTER PROCEDURE PROCMESCLARPRODUTOS (
    INCODIGOSUBSTITUTO TYPE OF CODIGOS,
    INCODIGOSUBSTITUIDO TYPE OF CODIGOS)
AS
declare variable fator type of fatoresconversao;
declare variable codunidademedida type of codigos;
declare variable codunidademedidasubstituta type of codigos;
declare variable codigonota type of codigos;
declare variable qtdesusbtituida type of quantidades;
declare variable valorsubstituido type of monetarios;
begin
    --Verifica se existem notas de mesmo cdigo para os dois produtos
    for select  tabitensnotasfiscais.codigonotafiscal
        from    tabitensnotasfiscais
        where   tabitensnotasfiscais.codigoproduto = :incodigosubstituto
        and     tabitensnotasfiscais.codigonotafiscal in
        (select t2.codigonotafiscal from  tabitensnotasfiscais t2 where t2.codigoproduto = :incodigosubstituido)
        into :codigonota
    do
    begin
        select  tabitensnotasfiscais.quantidade,
                tabitensnotasfiscais.valor,
                tabunidadesmedida.fatorconversao
        from    tabitensnotasfiscais, tabunidadesmedida
        where   tabitensnotasfiscais.codigonotafiscal = :codigonota
        and     tabitensnotasfiscais.codigoproduto    = :incodigosubstituido
        and     tabunidadesmedida.codigo              = tabitensnotasfiscais.codigounidademedida
        into    :qtdesusbtituida, :valorsubstituido, :fator;

        update  tabitensnotasfiscais
        set     tabitensnotasfiscais.quantidade       = (tabitensnotasfiscais.quantidade +(:qtdesusbtituida * :fator)),
                tabitensnotasfiscais.valor            = (tabitensnotasfiscais.valor + :valorsubstituido)
        where   tabitensnotasfiscais.codigoproduto    = :incodigosubstituto
        and     tabitensnotasfiscais.codigonotafiscal = :codigonota;

        delete  from tabitensnotasfiscais
        where   tabitensnotasfiscais.codigoproduto       = :incodigosubstituido
        and     tabitensnotasfiscais.codigonotafiscal    = :codigonota;
    end
    --Alterar todos os produtos a serem subsitudos na tabela de itensnotasfiscais
    for select  tabitensnotasfiscais.codigonotafiscal,
                tabitensnotasfiscais.quantidade,
                tabitensnotasfiscais.valor,
                tabunidadesmedida.fatorconversao
        from    tabitensnotasfiscais
        join    tabunidadesmedida on tabunidadesmedida.codigo = tabitensnotasfiscais.codigounidademedida
        and     tabitensnotasfiscais.codigoproduto = :incodigosubstituido
        into    :codigonota, :qtdesusbtituida, :valorsubstituido, :fator
    do
    begin
        select  tabunidadesmedida.codigo
        from    tabunidadesmedida
        where   tabunidadesmedida.codigoproduto = :incodigosubstituto
        and     tabunidadesmedida.tipo = 'PRINCIPAL'
        into    :codunidademedidasubstituta;

        update  tabitensnotasfiscais
        set     tabitensnotasfiscais.quantidade          = (tabitensnotasfiscais.quantidade * :fator),
                tabitensnotasfiscais.codigoproduto       = :incodigosubstituto,
                tabitensnotasfiscais.codigounidademedida = :codunidademedidasubstituta
        where   tabitensnotasfiscais.codigoproduto       = :incodigosubstituido
        and     tabitensnotasfiscais.codigonotafiscal    = :codigonota;
    end
    --Deleta o produto da tabela.
    delete  from tabprodutos
    where   tabprodutos.codigoproduto = :incodigosubstituido;

    post_event 'PRODUTOS';
end;


ALTER PROCEDURE PROCMESCLARPROPRIEDADES (
    INCODIGOSUBSTITUTO TYPE OF CODIGOS,
    INCODIGOSUBSTITUIDO TYPE OF CODIGOS)
AS
begin
    update  tabatendimentos
    set     tabatendimentos.codigopropriedade = :incodigosubstituto
    where   tabatendimentos.codigopropriedade = :incodigosubstituido;

    update  tabatividadespropriedades
    set     tabatividadespropriedades.codigopropriedade = :incodigosubstituto
    where   tabatividadespropriedades.codigopropriedade = :incodigosubstituido;

    update  tabnecessidadespropriedades
    set     tabnecessidadespropriedades.codigopropriedade = :incodigosubstituto
    where   tabnecessidadespropriedades.codigopropriedade = :incodigosubstituido;

    update  tabpessoaspropriedades
    set     tabpessoaspropriedades.codigopropriedade = :incodigosubstituto,
            tabpessoaspropriedades.responsavelpropriedade = 'N'
    where   tabpessoaspropriedades.codigopropriedade = :incodigosubstituido;

    update  tabrecursospropriedades
    set     tabrecursospropriedades.codigopropriedade = :incodigosubstituto
    where   tabrecursospropriedades.codigopropriedade = :incodigosubstituido;

    update  tabprocessos
    set     tabprocessos.codigopropriedade = :incodigosubstituto
    where   tabprocessos.codigopropriedade = :incodigosubstituido;

    delete  from tabpropriedades
    where   tabpropriedades.codigopropriedade = :incodigosubstituido;

    update tabpropriedades
    set    dataultimanota = (
      select max(tabpessoaspropriedades.dataultimanota)
      from   tabpessoaspropriedades
      where  tabpessoaspropriedades.codigopropriedade = :incodigosubstituto)
    where  tabpropriedades.codigopropriedade = :incodigosubstituto;

