ALTER PROCEDURE PROCPERSISTIRCADASTROPROCESSOS (
    INCODIGOPROCESSO INTEGER)
AS
declare variable CODIGOPRODUTORPROCESSO CODIGOS;
declare variable CODIGOPROPRIEDADEPROCESSO CODIGOS;
declare variable CODIGOPESSOAPRODUTOR CODIGOS;
declare variable CODIGOSOCIO CODIGOS;
declare variable TRATAMENTOSOCIO CLASSIFICADORES;
declare variable NUMEROINICIAL NUMEROSINTEIROS;
declare variable NUMEROFINAL NUMEROSINTEIROS;
declare variable NUMINICIALBLOCO NUMEROSINTEIROS;
declare variable NUMFINALBLOCO NUMEROSINTEIROS;
declare variable NUMNOTAS integer;
declare variable NUMNOTASBLOCO integer;
declare variable QUANTIDADEBLOCOS integer;
declare variable BLOCO integer;
declare variable DATAAUTORIZACAO type of DATAS;
declare variable NUMEROAIDF NUMEROSDOCUMENTOS;
declare variable SITUACAOINSCRICAO ENUMERACOES;
declare variable DATAVALIDADEBLOCO DATAS;
declare variable CODIGOFICHA type of CODIGOS;
declare variable TRATAMENTOFICHA type of CLASSIFICADORES;
BEGIN
   select TABPROCESSOS.CODIGOPRODUTOR, TABPROCESSOS.CODIGOPROPRIEDADE, TABPESSOASPROPRIEDADES.CODIGOPESSOA
   from   TABPROCESSOS
   join   TABPESSOASPROPRIEDADES on TABPESSOASPROPRIEDADES.CODIGO = TABPROCESSOS.CODIGOPRODUTOR
   where  CODIGOPROCESSO = :INCODIGOPROCESSO
   into   :CODIGOPRODUTORPROCESSO, :CODIGOPROPRIEDADEPROCESSO, :CODIGOPESSOAPRODUTOR;

   -- PRODUTOR
   select iif(TIPOPROCESSO = 'CANCELAMENTO DE IE', '0', '1')
   from   TABPROCESSOS
   where  CODIGOPROCESSO = :INCODIGOPROCESSO
   into   :SITUACAOINSCRICAO;

   update TABPESSOASPROPRIEDADES
   set    SITUACAO = :SITUACAOINSCRICAO,
          CADASTROATUALIZADO = 'S'
   where  CODIGO = :CODIGOPRODUTORPROCESSO;

   update TABPESSOASPROPRIEDADES
   set    SITUACAO = '0'
   where  TABPESSOASPROPRIEDADES.CODIGOPROPRIEDADE = :CODIGOPROPRIEDADEPROCESSO
   and    TABPESSOASPROPRIEDADES.CODIGOPESSOA = :CODIGOPESSOAPRODUTOR
   and    TABPESSOASPROPRIEDADES.CODIGO <> :CODIGOPRODUTORPROCESSO;

   -- FICHA COMPLEMENTAR
   update TABFICHASCOMPLEMENTARES
   set    CODIGOPRODUTOR = :CODIGOPRODUTORPROCESSO
   where  CODIGO in (
            select TABFCAPROCESSOS.CODIGOFICHA
            from   TABFCAPROCESSOS
            where  TABFCAPROCESSOS.CODIGOPROCESSO = :INCODIGOPROCESSO
          );

   update TABFCAPROCESSOS
   set    FICHAATUALIZADA = 'S'
   where  (CODIGOPROCESSO = :INCODIGOPROCESSO);

   for select   tabfcaprocessos.codigoficha, tabfcaprocessos.tratamento
       from     tabfcaprocessos
       where    tabfcaprocessos.codigoprocesso = :incodigoprocesso
       into     :codigoficha, :tratamentoficha
   do begin
       if (:codigoficha > 0 and :tratamentoficha <> 'EXCLUIR') then begin
         update or insert into tabfcaprodutores (codigoficha, codigoprodutor)
         values (:codigoficha, :codigoprodutorprocesso)
         matching (codigoficha, codigoprodutor);
       end
       if (:codigoficha > 0 and :tratamentoficha = 'EXCLUIR') then begin
         delete from tabfcaprodutores
         where  tabfcaprodutores.codigoficha = :codigoficha
         and    tabfcaprodutores.codigoprodutor = :codigoprodutorprocesso;
       end
   end
   -- SCIOS
   for select TABSOCIOSPROCESSOS.CODIGOSOCIO, TABSOCIOSPROCESSOS.TRATAMENTO
       from   TABSOCIOSPROCESSOS
       where  TABSOCIOSPROCESSOS.CODIGOPROCESSO = :INCODIGOPROCESSO
       into   :CODIGOSOCIO, :TRATAMENTOSOCIO
   do begin
      if (:TRATAMENTOSOCIO = 'EXCLUIR') then begin
         delete from TABSOCIOS
         where  TABSOCIOS.CODIGOSOCIO = :CODIGOSOCIO and
                TABSOCIOS.CODIGOPESSOAPROPRIEDADE = :CODIGOPRODUTORPROCESSO;
      end
      else begin
         update or insert into TABSOCIOS (CODIGOSOCIO, CODIGOPESSOAPROPRIEDADE)
         values(:CODIGOSOCIO, :CODIGOPRODUTORPROCESSO);
      end
   end

   -- TALES
   for select TABITENSAIDF.NUMEROINICIAL, TABITENSAIDF.NUMEROFINAL,
              TABITENSAIDF.QUANTIDADEBLOCOS,
              tabaidfs.dataautorizacao, TABAIDFS.DATAVALIDADE,
              TABAIDFS.NUMEROAUTORIZACAO
       from   TABITENSAIDF, TABAIDFS, TABENTREGASTALOES
       where  TABITENSAIDF.CODIGOAIDF = TABAIDFS.CODIGOAIDF and
              TABAIDFS.CODIGOPROCESSO = TABENTREGASTALOES.CODIGOPROCESSO and
              TABAIDFS.CODIGOPROCESSO = :INCODIGOPROCESSO
       into   :NUMEROINICIAL, :NUMEROFINAL, :QUANTIDADEBLOCOS, :dataautorizacao, :DATAVALIDADEBLOCO, :NUMEROAIDF
   do begin
      NUMNOTAS = NUMEROFINAL - NUMEROINICIAL + 1;
      NUMNOTASBLOCO = NUMNOTAS / QUANTIDADEBLOCOS;
      NUMINICIALBLOCO = NUMEROINICIAL;
      NUMFINALBLOCO = NUMINICIALBLOCO + NUMNOTASBLOCO - 1;

      BLOCO = 1;
      while (BLOCO <= QUANTIDADEBLOCOS) do begin
         update or insert into TABTALOES (NUMEROINICIAL, 
                                          NUMEROFINAL, 
                                          dataautorizacao,
                                          DATAVALIDADE,
                                          NUMEROAIDF, 
                                          CODIGOPESSOAPROPRIEDADE)
         values (:NUMINICIALBLOCO,
                 :NUMFINALBLOCO,
                 :dataautorizacao,
                 :DATAVALIDADEBLOCO,
                 :NUMEROAIDF, 
                 :CODIGOPRODUTORPROCESSO)
         matching (NUMEROINICIAL, NUMEROFINAL, NUMEROAIDF, CODIGOPESSOAPROPRIEDADE);

         BLOCO = BLOCO + 1;
         NUMINICIALBLOCO = NUMFINALBLOCO + 1;
         NUMFINALBLOCO = NUMINICIALBLOCO + NUMNOTASBLOCO - 1;
      end
   end

   update TABPROCESSOS
   set    CADASTROATUALIZADO = 'S',
          DATATERMINO = current_date,
          SITUACAO = 'CONCLUDO COM SUCESSO'
   where  CODIGOPROCESSO = :INCODIGOPROCESSO;
END;


