ALTER PROCEDURE PROCRETIRAFORMATACAO (
    INNUMERODOC TYPE OF NUMEROSDOCUMENTOS)
RETURNS (
    OUTDIGITOS TYPE OF NUMEROSDOCUMENTOS)
AS
declare variable I integer;
declare variable CARACTER char(1);
declare variable COMPRIMENTO integer;
begin
   COMPRIMENTO = char_length(INNUMERODOC);
   I = 1;
   OUTDIGITOS = '';

   while (I <= COMPRIMENTO) do begin
      CARACTER = trim(substring(INNUMERODOC from I for 1));
      if (CARACTER between '0' and '9' ) then begin
         OUTDIGITOS = OUTDIGITOS || CARACTER;
      end
      I = I + 1;
   end
   suspend;
end;




